@ExceptionHandler(value = { MethodArgumentNotValidException.class, HttpMessageNotReadableException.class })
public <T extends BindException> ResponseEntity<ExceptionMsg> handleValidationException(final T e) {
    log.info("**ApiExceptionHandler controller, handle validation exception*\n");
    final var badRequest = HttpStatus.BAD_REQUEST;
    return new ResponseEntity<>(ExceptionMsg.builder().msg("*" + e.getBindingResult().getFieldError().getDefaultMessage() + "!**").httpStatus(badRequest).timestamp(ZonedDateTime.now(ZoneId.systemDefault())).build(), badRequest);
}

@ExceptionHandler(value = { CartNotFoundException.class, OrderNotFoundException.class, IllegalStateException.class })
public <T extends RuntimeException> ResponseEntity<ExceptionMsg> handleApiRequestException(final T e) {
    log.info("**ApiExceptionHandler controller, handle API request*\n");
    final var badRequest = HttpStatus.BAD_REQUEST;
    return new ResponseEntity<>(ExceptionMsg.builder().msg("#### " + e.getMessage() + "! ####").httpStatus(badRequest).timestamp(ZonedDateTime.now(ZoneId.systemDefault())).build(), badRequest);
}

