    return OrderItemDto.builder().productId(orderItem.getProductId()).orderId(orderItem.getOrderId()).orderedQuantity(orderItem.getOrderedQuantity()).productDto(ProductDto.builder().productId(orderItem.getProductId()).build()).orderDto(OrderDto.builder().orderId(orderItem.getOrderId()).build()).build();
}

public static OrderItem map(final OrderItemDto orderItemDto) {
