}

@Bean
public ObjectMapper objectMapperBean() {
    return new JsonMapper().enable(SerializationFeature.INDENT_OUTPUT);
}

@Override
public List<OrderItemDto> findAll() {
    log.info("*** OrderItemDto List, service; fetch all orderItems *");
    return this.orderItemRepository.findAll().stream().map(OrderItemMappingHelper::map).map(o -> {
        o.setProductDto(this.restTemplate.getForObject(AppConstant.DiscoveredDomainsApi.PRODUCT_SERVICE_API_URL + "/" + o.getProductDto().getProductId(), ProductDto.class));
        o.setOrderDto(this.restTemplate.getForObject(AppConstant.DiscoveredDomainsApi.ORDER_SERVICE_API_URL + "/" + o.getOrderDto().getOrderId(), OrderDto.class));
        return o;
    }).distinct().collect(Collectors.toUnmodifiableList());
}

@Override
public OrderItemDto findById(final OrderItemId orderItemId) {
    log.info("*** OrderItemDto, service; fetch orderItem by id *");
    return this.orderItemRepository.findById(null).map(OrderItemMappingHelper::map).map(o -> {
        o.setProductDto(this.restTemplate.getForObject(AppConstant.DiscoveredDomainsApi.PRODUCT_SERVICE_API_URL + "/" + o.getProductDto().getProductId(), ProductDto.class));
        o.setOrderDto(this.restTemplate.getForObject(AppConstant.DiscoveredDomainsApi.ORDER_SERVICE_API_URL + "/" + o.getOrderDto().getOrderId(), OrderDto.class));
        return o;
    }).orElseThrow(() -> new OrderItemNotFoundException(String.format("OrderItem with id: %s not found", orderItemId)));
}

@Override
public OrderItemDto save(final OrderItemDto orderItemDto) {
    log.info("*** OrderItemDto, service; save orderItem *");
    return OrderItemMappingHelper.map(this.orderItemRepository.save(OrderItemMappingHelper.map(orderItemDto)));
}

